import React, { Component } from 'react'

class ToDoForm extends Component {

    state = {
        ...this.returnStateObject()
    }

    returnStateObject() {
        if (this.props.currentIndex == -1)
            return {
                todo: ''
            }
        else
            return this.props.list[this.props.currentIndex]
    }

    componentDidUpdate(prevProps) {
        if (prevProps.currentIndex != this.props.currentIndex || prevProps.list != this.props.list) {
            this.setState({ ...this.returnStateObject() })
            console.log(prevProps, this.props)
        }
    }

    handleInputChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    handleSubmit = (e) => {
        e.preventDefault()
        this.props.onAddOrEdit(this.state)
    }

    render() {
        return (
            <form onSubmit={this.handleSubmit} autoComplete="off">   
                <input style={{width:"40%",margin:10,border:0,backgroundColor:"#F8F8F8",borderRadius:6,padding:5,fontSize:16}} name="todo" placeholder="Enter Task" onChange={this.handleInputChange} value={this.state.todo} />
                <button style={{margin:10,border:0,padding:5,borderRadius:6,width:70,backgroundColor:"#338AFF",color:"white"}}type="submit">Add</button>
                <br/>
            </form>
        )
    }
}

export default ToDoForm
